using MySqlConnector;
using RiotProxy.External.Domain.Enums;
using RiotProxy.External.Domain.Entities;

namespace RiotProxy.Infrastructure.External.Database.Repositories
{
    public class UserRepository
    {
        private readonly IDbConnectionFactory _factory;

        public UserRepository(IDbConnectionFactory factory)
        {
            _factory = factory;
        }

        public async Task<IList<User>> GetAllUsersAsync()
        {
            var users = new List<User>();

            await using var conn = _factory.CreateConnection();
            await conn.OpenAsync();

            const string sql = "SELECT UserId, UserName, UserType FROM User";
            await using var cmd = new MySqlCommand(sql, conn);

            await using var reader = await cmd.ExecuteReaderAsync();
            while (await reader.ReadAsync())
            {
                users.Add(new User
                {
                    UserId = reader.GetInt32(0),
                    UserName = reader.GetString(1),
                    UserType = (UserTypeEnum)reader.GetInt32(2)
                });
            }

            return users;
        }

        public async Task<User?> GetByUserNameAsync(string userName)
        {
            await using var conn = _factory.CreateConnection();

            await conn.OpenAsync();

            const string sql = "SELECT UserId, UserName FROM User WHERE UserName = @userName";
            await using var cmd = new MySqlCommand(sql, conn);
            cmd.Parameters.AddWithValue("@userName", userName);

            await using var reader = await cmd.ExecuteReaderAsync();
            if (!await reader.ReadAsync()) return null;

            return new User
            {
                UserId = reader.GetInt32(0),
                UserName = reader.GetString(1),
                UserType = Enum.Parse<UserTypeEnum>(reader.GetString(2))
            };
        }
        
        public async Task<User?> CreateUserAsync(string userName, UserTypeEnum userType)
        {
            await using var conn = _factory.CreateConnection();
            await conn.OpenAsync();

            //    We only insert the UserName; UserId is generated by the DB.
            const string sql = @"
                INSERT INTO User (UserName, UserType)          
                VALUES (@userName, @userType);
                SELECT LAST_INSERT_ID();             
            ";
            await using var cmd = new MySqlCommand(sql, conn);
            cmd.Parameters.AddWithValue("@userName", userName);
            cmd.Parameters.AddWithValue("@userType", (int)userType);
            //    ExecuteScalarAsync returns the first column of the first row,
            //    which is the value produced by LAST_INSERT_ID().
            var result = await cmd.ExecuteScalarAsync();
            
            // If the insert failed for any reason, result could be null.
            if (result == null || result == DBNull.Value)
                return null;

            // 4️⃣ Build and return the User object.
            var newUser = new User
            {
                UserId   = Convert.ToInt32(result), // the auto‑generated id
                UserName = userName,
                UserType = userType
            };

            return newUser;
        }
    }
}